        public HrmServiceController(string serviceIP, string servicePort, string serviceFolderPath)
        {
            this.serviceIP = serviceIP;
            this.servicePort = servicePort;
            serviceAddress = "http://" + serviceIP + ((!string.IsNullOrEmpty(servicePort)) ? ":" : "") + servicePort + serviceFolderPath + "/HrmService.svc";

